import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function InventoryDashboard() {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    stock: "",
  });
  const [editingProduct, setEditingProduct] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const navigate = useNavigate();

  // Obtener productos del backend
  const fetchProducts = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:5000/api/products", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProducts(response.data);
    } catch (error) {
      console.error("Error al obtener productos:", error);
      alert("Error al obtener productos. Por favor, inicia sesión de nuevo.");
      logout();
    }
  };

  // Cerrar sesión
  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Enviar formulario para agregar o editar productos
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");

      if (editingProduct) {
        await axios.put(
          `http://localhost:5000/api/products/${editingProduct._id}`,
          formData,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        alert("Producto actualizado correctamente.");
      } else {
        await axios.post("http://localhost:5000/api/products", formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert("Producto agregado correctamente.");
      }

      setFormData({ name: "", description: "", price: "", category: "", stock: "" });
      setEditingProduct(null);
      setIsModalOpen(false);
      fetchProducts();
    } catch (error) {
      console.error("Error al guardar producto:", error);
      alert("Error al guardar producto.");
    }
  };

  // Eliminar producto
  const handleDelete = async (productId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:5000/api/products/${productId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Producto eliminado correctamente.");
      fetchProducts();
    } catch (error) {
      console.error("Error al eliminar producto:", error);
      alert("Error al eliminar producto.");
    }
  };

  const openModal = (product = null) => {
    if (product) {
      setEditingProduct(product);
      setFormData({
        name: product.name,
        description: product.description,
        price: product.price,
        category: product.category,
        stock: product.stock,
      });
    } else {
      setEditingProduct(null);
      setFormData({ name: "", description: "", price: "", category: "", stock: "" });
    }
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setEditingProduct(null);
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Estadísticas
  const totalProducts = products.length;
  const lowStockProducts = products.filter((product) => product.stock < 10).length;
  const uniqueCategories = [...new Set(products.map((product) => product.category))].length;

  return (
    <div className="min-h-screen bg-gray-100 flex">
      {/* Menú lateral */}
      <div className="w-64 bg-blue-600 text-white p-4">
        <h1 className="text-2xl font-bold mb-6">Gestor de Inventarios</h1>
        <ul className="space-y-4">
          <li className="hover:bg-blue-700 p-2 rounded">Inicio</li>
          <li className="hover:bg-blue-700 p-2 rounded">Productos</li>
          <li className="hover:bg-blue-700 p-2 rounded">Categorías</li>
          <li className="hover:bg-blue-700 p-2 rounded">Configuración</li>
        </ul>
      </div>

      {/* Contenido principal */}
      <div className="flex-1 p-6">
        {/* Estadísticas rápidas */}
        <div className="grid grid-cols-3 gap-4 mb-6">
          <div className="bg-white rounded-lg shadow-lg p-4">
            <h2 className="text-lg font-semibold">Total de Productos</h2>
            <p className="text-2xl font-bold">{totalProducts}</p>
          </div>
          <div className="bg-white rounded-lg shadow-lg p-4">
            <h2 className="text-lg font-semibold">Productos en Baja Existencia</h2>
            <p className="text-2xl font-bold">{lowStockProducts}</p>
          </div>
          <div className="bg-white rounded-lg shadow-lg p-4">
            <h2 className="text-lg font-semibold">Categorías Activas</h2>
            <p className="text-2xl font-bold">{uniqueCategories}</p>
          </div>
        </div>

        {/* Botón para agregar producto */}
        <div className="mb-4 flex justify-end">
          <button
            onClick={() => openModal()}
            className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
          >
            Agregar Producto
          </button>
        </div>

        {/* Tabla de productos */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Lista de Productos</h2>
          {products.length > 0 ? (
            <table className="w-full text-left border-collapse">
              <thead>
                <tr>
                  <th className="border-b p-2">Nombre</th>
                  <th className="border-b p-2">Descripción</th>
                  <th className="border-b p-2">Precio</th>
                  <th className="border-b p-2">Categoría</th>
                  <th className="border-b p-2">Cantidad</th>
                  <th className="border-b p-2">Acciones</th>
                </tr>
              </thead>
              <tbody>
                {products.map((product) => (
                  <tr key={product._id}>
                    <td className="border-b p-2">{product.name}</td>
                    <td className="border-b p-2">{product.description}</td>
                    <td className="border-b p-2">{product.price}</td>
                    <td className="border-b p-2">{product.category}</td>
                    <td
                      className={`border-b p-2 ${
                        product.stock < 10 ? "text-red-500 font-bold" : ""
                      }`}
                    >
                      {product.stock}
                    </td>
                    <td className="border-b p-2 flex gap-2">
                      <button
                        onClick={() => openModal(product)}
                        className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600"
                      >
                        Editar
                      </button>
                      <button
                        onClick={() => handleDelete(product._id)}
                        className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No hay productos disponibles.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default InventoryDashboard;
