import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  // Función para obtener los productos desde el backend
  const fetchProducts = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/products', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProducts(response.data);
    } catch (error) {
      console.error('Error al obtener los productos:', error);
      alert('Error al obtener los productos. Por favor, inicia sesión de nuevo.');
      logout();
    }
  };

  // Función para cerrar sesión
  const logout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Bienvenido al Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600"
        >
          Cerrar sesión
        </button>
      </div>
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Lista de Productos</h2>
        {products.length > 0 ? (
          <table className="w-full text-left border-collapse">
            <thead>
              <tr>
                <th className="border-b p-2">Nombre</th>
                <th className="border-b p-2">Descripción</th>
                <th className="border-b p-2">Precio</th>
                <th className="border-b p-2">Categoría</th>
                <th className="border-b p-2">Cantidad</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product._id}>
                  <td className="border-b p-2">{product.name}</td>
                  <td className="border-b p-2">{product.description}</td>
                  <td className="border-b p-2">{product.price}</td>
                  <td className="border-b p-2">{product.category}</td>
                  <td className="border-b p-2">{product.stock}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No hay productos disponibles.</p>
        )}
      </div>
    </div>   
);
const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    stock: '',
  });
  
  // Manejar cambios en el formulario
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };
  
  // Función para enviar un nuevo producto al backend
  const addProduct = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/products', newProduct, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts(); // Actualizar la lista de productos
      setNewProduct({ name: '', description: '', price: '', category: '', stock: '' }); // Limpiar formulario
    } catch (error) {
      console.error('Error al agregar el producto:', error);
      alert('No se pudo agregar el producto.');
    }
  };
  
  // Añade el siguiente formulario en el JSX de Dashboard
  <div className="mb-6">
    <h2 className="text-xl font-semibold mb-4">Agregar Producto</h2>
    <form onSubmit={addProduct} className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
      <input
        type="text"
        name="name"
        placeholder="Nombre"
        value={newProduct.name}
        onChange={handleInputChange}
        className="border p-2 rounded-md"
        required
      />
      <input
        type="text"
        name="description"
        placeholder="Descripción"
        value={newProduct.description}
        onChange={handleInputChange}
        className="border p-2 rounded-md"
        required
      />
      <input
        type="number"
        name="price"
        placeholder="Precio"
        value={newProduct.price}
        onChange={handleInputChange}
        className="border p-2 rounded-md"
        required
      />
      <input
        type="text"
        name="category"
        placeholder="Categoría"
        value={newProduct.category}
        onChange={handleInputChange}
        className="border p-2 rounded-md"
        required
      />
      <input
        type="number"
        name="stock"
        placeholder="Cantidad"
        value={newProduct.stock}
        onChange={handleInputChange}
        className="border p-2 rounded-md"
        required
      />
      <button
        type="submit"
        className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
      >
        Agregar Producto
      </button>
    </form>
  </div>
  {products.map((product) => (
    <tr key={product._id}>
      <td className="border-b p-2">{product.name}</td>
      <td className="border-b p-2">{product.description}</td>
      <td className="border-b p-2">{product.price}</td>
      <td className="border-b p-2">{product.category}</td>
      <td className="border-b p-2">{product.stock}</td>
      <td className="border-b p-2">
        <button
          onClick={() => setEditingProduct(product)}
          className="bg-yellow-500 text-white px-2 py-1 rounded-md hover:bg-yellow-600"
        >
          Editar
        </button>
      </td>
    </tr>
  ))}
  const [editingProduct, setEditingProduct] = useState(null);

// Manejar cambios en el formulario de edición
const handleEditInputChange = (e) => {
  const { name, value } = e.target;
  setEditingProduct({ ...editingProduct, [name]: value });
};

// Función para guardar cambios
const saveEditProduct = async (e) => {
  e.preventDefault();
  try {
    const token = localStorage.getItem('token');
    await axios.put(`http://localhost:5000/api/products/${editingProduct._id}`, editingProduct, {
      headers: { Authorization: `Bearer ${token}` },
    });
    fetchProducts(); // Actualizar lista
    setEditingProduct(null); // Cerrar modal
  } catch (error) {
    console.error('Error al editar el producto:', error);
    alert('No se pudo editar el producto.');
  }
};

// JSX para el modal
{editingProduct && (
  <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
    <div className="bg-white p-6 rounded-md shadow-md">
      <h2 className="text-xl font-semibold mb-4">Editar Producto</h2>
      <form onSubmit={saveEditProduct}>
        <input
          type="text"
          name="name"
          value={editingProduct.name}
          onChange={handleEditInputChange}
          className="border p-2 rounded-md mb-2 w-full"
          required
        />
        {/* Repite los campos para descripción, precio, etc. */}
        <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">
          Guardar
        </button>
        <button
          onClick={() => setEditingProduct(null)}
          className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 ml-2"
        >
          Cancelar
        </button>
      </form>
    </div>
  </div>
)}
<td className="border-b p-2">
  <button
    onClick={() => deleteProduct(product._id)}
    className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
  >
    Eliminar
  </button>
</td>
const deleteProduct = async (id) => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5000/api/products/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchProducts(); // Actualizar lista
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
      alert('No se pudo eliminar el producto.');
    }
  };
  
}

export default Dashboard;
